mapper.insert:
INSERT INTO agency_board
(
	user_id
  , title 
  , agency 
  , ps_num 
  , category 
  , image0 
  , head 
  , body 
  , image1 
  , youtube 
  , cl_addr 
  , start_date 
  , end_date 
  , start_time 
  , end_time 
)
VALUES
(
	#{user_id}
  , #{title}
  , #{agency}
  , #{ps_num}
  , #{category}
  , #{image0}
  , #{head}
  , #{body}
  , #{image1}
  , #{youtube}
  , #{cl_addr}
  , #{start_date}
  , #{end_date}
  , #{start_time}
  , #{end_time}
)

mapper.select:
SELECT content_no , user_id , title , agency , ps_num , category , write_date , image0 , head , body , image1 , youtube , cl_addr , start_date , end_date , start_time , end_time FROM agency_board WHERE end_date >= DATE_FORMAT(now(), '%Y-%m-%d') AND board_condition = 'Y'
AND category = #{category}
ORDER BY write_date DESC

SELECT content_no , user_id , title , agency , ps_num , category , write_date , image0 , head , body , image1 , youtube , cl_addr , start_date , end_date , start_time , end_time FROM agency_board WHERE end_date < DATE_FORMAT(now(), '%Y-%m-%d') AND board_condition = 'Y'
AND category = #{category}
ORDER BY write_date DESC

select COALESCE(SUM(reserve_num),0)
  FROM reservation 
 WHERE content_no = #{content_no}
   AND reserve_condition = 'Y'
    
   AND class_date = #{class_date}

mapper.insert:
INSERT INTO reservation
(
	user_id
  , content_no 
  , ps_num 
  , class_date 
  , reserve_num 
)
VALUES
(
	#{user_id} 
  , #{content_no}
  , #{ps_num}
  , #{class_date}
  , #{reserve_num}
)

INSERT INTO favorite
(
	content_no
  , user_id 
)
VALUES
(
	#{content_no}
  , #{user_id}
)

mapper.update:
UPDATE favorite
   SET favorite_condition = 'Y'
     , favorite_date = CURRENT_TIMESTAMP
 WHERE content_no = #{content_no}
   AND user_id = #{user_id}

UPDATE favorite
   SET favorite_condition = 'N'
     , favorite_date = CURRENT_TIMESTAMP
 WHERE content_no = #{content_no}
   AND user_id = #{user_id}

